import React, {Component} from "react";
import PropTypes from "prop-types";
import DisplayChild from "./DisplayChild";

class AnnualCalculations extends Component {
state = {
  ANNUALPAYOUT: 1
}

//yearlyPayOut = 9*payOut
// if (grantValue) THEN loan = payOut*0.7

// =============== Calculations ===============================================
componentDidMount = () => {
  this.calculateAnnualPayout();
}

componentDidUpdate = (prevProps) => {
  if (prevProps !== this.props ) {
    this.calculateAnnualPayout();
  };
}

// =============== Calculate annual payout ===================================

calculateAnnualPayout = () => {
  this.setState({ ANNUALPAYOUT: 9*this.props.PAYOUT});
};

render() {

  return (

    <div>
        <DisplayChild
          func={this.state.ANNUALPAYOUT}
          text={"Útborgað á ári"}
        />
    </div>
  );
}
}

export default AnnualCalculations
